import axios from 'axios'

const actions = {

    getAllPosts : async() => {
        return await axios.get(`http://localhost:5000/api/getStrings`)
    },
    addPosts: async(Login) => {
        return await axios.post(`http://localhost:5000/api/login`, {login})
    }
}

export default actions
