import axios from 'axios'


let resetHead = () => {
    return { 
        headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
        }
    }
}

const actions = {

    getAllPosts : async() => {
        return await axios.get(`http://localhost:5000/api/getStrings`, reset)
    },

    addPosts: async(login) => {
        return await axios.post(`http://localhost:5000/api/login`, {login})
    },

    logIn : async(data) => {
        return await axios.post(`http://localhost:500/api/logIn`, data, resetHead())
    }
}

export default actions
